mapper.insert:
insert into event_request (		
	eday_idx,
	pon,
	store_idx
) values (
	#{eday_idx},
	0,
	#{store_idx}
)

mapper.delete:
DELETE FROM event_request
WHERE eday_idx = #{edayIdx}
AND store_idx = #{storeIdx}

mapper.select:
SELECT event_idx
FROM event_day
WHERE eday_idx = #{edayIdx}

SELECT eday_idx
FROM event_request
WHERE store_idx = #{store_idx}
