mapper.select:
	select * from store

		 where store_idx=#{value} 
 		 where store_name=#{value}
 		 where store_area=#{value}

	order by store_idx

mapper.update:
update store set 
	store_hidden = 1
where store_idx = #{store_idx}

update store set 
	store_hidden = 0
where store_idx = #{store_idx}

update store set 
	store_hidden = 2
where store_idx = #{store_idx}

update store set
	store_like_cnt = store_like_cnt + 1
where store_idx = #{store_idx}

update store set
    store_like_cnt = GREATEST(store_like_cnt - 1, 0)
where store_idx = #{store_idx}
